 
#include "iostream"
#include "fstream"
#include "istream"
 
using namespace std;


// создает бинарник по строке
int main(int argc, char * argv[])
{
    string j = "";//строка, 6 элементов
 
    ofstream f("input.bin", ios::binary | ios::trunc | ios::out); // тут нужно побитовое ИЛИ, а не логическое
    

    for (int i = 0; 2*i + 1 < j.length(); i++) {
        int byte;
        sscanf(j.substr(2*i,2).c_str(), "%x", &byte);
        j[i] = (char)byte;
    }
    f.write(j.c_str(), j.length()/2); // нельзя просто так взять адрес объекта, нужно сохранять содержимое строки
    f.flush(); // не забудем сбросить данные на диск, а то рискуем получить пустую строку
 
    char cstrin[7]; // 7й - это ноль
 
    fstream f1("input.bin", ios::binary | ios::in); // тут нужно побитовое ИЛИ, а не логическое
 
    f1.read(cstrin, 7); // читаем вместе с нулем
 
    cout << cstrin;
 
 
    return 0;
}